export class Responses {
  constructor(client) {
    this._client = client;
  }

  async create(params) {
    return this._client.post('/responses', params);
  }

  stream(params) {
    return this._client.stream('/responses', params);
  }

  async parse(params) {
    return this._client.post('/responses/parse', params);
  }
}
